'use client';

import { useState, useEffect } from 'react';
import { AnimatePresence, motion } from 'framer-motion';
import HeroDemo from "@/components/ui/demo";
import AboutSection from "@/components/ui/about-section";
import ProjectsSection from "@/components/ui/projects-section";
import ContactSection from "@/components/ui/contact-section";
import Footer from "@/components/ui/footer";
import { PageLoader } from "@/components/ui/page-loader";



export default function Home() {
  // null = not yet determined (avoids hydration mismatch)
  // true = show loader, false = show content
  const [loading, setLoading] = useState<boolean | null>(null);

  useEffect(() => {
    const seen = sessionStorage.getItem('loader-seen');
    // Show loader if never seen before
    setLoading(!seen);
  }, []);

  const handleLoaderComplete = () => {
    sessionStorage.setItem('loader-seen', '1');
    setLoading(false);
  };

  // Don't render anything until we know whether to show loader
  // (prevents flash of content before loader)
  if (loading === null) {
    return <div className="fixed inset-0 bg-[#0a0a0a]" />;
  }

  return (
    <>
      <AnimatePresence mode="wait">
        {loading && (
          <PageLoader key="loader" onComplete={handleLoaderComplete} />
        )}
      </AnimatePresence>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: loading ? 0 : 1 }}
        transition={{ duration: 0.6, ease: 'easeOut' }}
      >
        <HeroDemo isLoaded={!loading} />
        <AboutSection />
        <ProjectsSection />
        <ContactSection />
        <Footer />
      </motion.div>
    </>
  );
}

