interface MinimalistHeroProps {
    logoText?: string;
    navLinks: { label: string; href: string }[];
    mainText: string;
    readMoreLink: string;
    imageSrc: string;
    imageAlt: string;
    overlayText: {
        part1: string;
        part2: string;
    };
    socialLinks: { icon: LucideIcon; href: string }[];
    locationText: string;
    className?: string;
    themeToggle?: React.ReactNode;
    isLoaded?: boolean;
}

const NavLink = ({
    href,
    children,
    isActive
}: {
    href: string;
    children: React.ReactNode;
    isActive: boolean;
}) => (
    <a
        href={href}
        className={cn(
            "text-sm font-medium tracking-widest transition-colors relative",
            isActive
                ? "text-foreground"
                : "text-foreground/60 hover:text-foreground"
        )}
    >
        {children}
        {isActive && (
            <motion.div
                layoutId="activeSection"
                className="absolute -bottom-1 left-0 right-0 h-0.5 bg-foreground"
                initial={false}
                transition={{ type: "spring", stiffness: 380, damping: 30 }}
            />
        )}
    </a>
);

const SocialIcon = ({ href, icon: Icon }: { href: string; icon: LucideIcon }) => (
    <a href={href} target="_blank" rel="noopener noreferrer" className="text-foreground/60 transition-colors hover:text-foreground">
        <Icon className="h-5 w-5" />
    </a>
);

const LogoWithName = () => {
    const [open, setOpen] = useState(false);
    const ref = useRef<HTMLDivElement>(null);

    useEffect(() => {
        const handleClickOutside = (e: MouseEvent) => {
            if (ref.current && !ref.current.contains(e.target as Node)) {
                setOpen(false);
            }
        };
        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    return (
        <div ref={ref} className="flex items-center gap-3">
            <motion.button
                onClick={() => setOpen(prev => !prev)}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                whileHover={{ scale: 1.12, filter: 'drop-shadow(0 0 12px rgba(255,255,255,0.55))' }}
                whileTap={{ scale: 0.95 }}
                transition={{ duration: 0.5 }}
                className="block cursor-pointer focus:outline-none"
                aria-label="Toggle name"
            >
                <img
                    src="/favicon.png"
                    alt="Logo"
                    className="h-10 w-10 rounded-full object-cover"
                />
            </motion.button>

            <motion.span
                initial={{ opacity: 0, x: -10, width: 0 }}
                animate={open
                    ? { opacity: 1, x: 0, width: 'auto' }
                    : { opacity: 0, x: -10, width: 0 }
                }
                transition={{ duration: 0.35, ease: [0.22, 1, 0.36, 1] }}
                className="overflow-hidden whitespace-nowrap text-sm font-semibold tracking-widest text-foreground uppercase"
            >
                ASHHAR SHAHAN
            </motion.span>
        </div>
    );
};

const SplitText = ({ text, delay = 0 }: { text: string; delay?: number }) => {
    const letters = text.split('');
    return (
        <>
            {letters.map((char, i) => (
                <motion.span
                    key={i}
                    initial={{ opacity: 0, y: 40 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{
                        duration: 0.5,
                        delay: delay + i * 0.04,
                        ease: [0.22, 1, 0.36, 1]
                    }}
                    className="inline-block"
                    style={{ whiteSpace: char === ' ' ? 'pre' : 'normal' }}
                >
                    {char === ' ' ? '\u00A0' : char}
                </motion.span>
            ))}
        </>
    );
};

export const MinimalistHero = ({
    logoText,
    navLinks,
    mainText,
    readMoreLink,
    imageSrc,
    imageAlt,
    overlayText,
    socialLinks,
    locationText,
    className,
    themeToggle,
    isLoaded = true,
}: MinimalistHeroProps) => {
    const [activeSection, setActiveSection] = useState('home');
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
    const containerRef = useRef<HTMLDivElement>(null);

    const { scrollYProgress } = useScroll({
        target: containerRef,
        offset: ['start start', 'end start']
    });

    const imageY = useTransform(scrollYProgress, [0, 1], [0, -80]);
    const textLeftY = useTransform(scrollYProgress, [0, 1], [0, -40]);
    const textRightY = useTransform(scrollYProgress, [0, 1], [0, -60]);
    const circleScale = useTransform(scrollYProgress, [0, 1], [1, 1.15]);
    const circleOpacity = useTransform(scrollYProgress, [0, 0.8], [1, 0]);

    const smoothImageY = useSpring(imageY, { stiffness: 100, damping: 30 });
    const smoothTextLeftY = useSpring(textLeftY, { stiffness: 80, damping: 25 });
    const smoothTextRightY = useSpring(textRightY, { stiffness: 80, damping: 25 });

    useEffect(() => {
        const handleScroll = () => {
            const sections = navLinks.map(link => link.href.replace('#', ''));
            const scrollPosition = window.scrollY + 100;

            for (const section of sections) {
                const element = document.getElementById(section);
                if (element) {
                    const { offsetTop, offsetHeight } = element;
                    if (scrollPosition >= offsetTop && scrollPosition < offsetTop + offsetHeight) {
                        setActiveSection(section);
                        break;
                    }
                }
            }
        };

        window.addEventListener('scroll', handleScroll);
        handleScroll();
        return () => window.removeEventListener('scroll', handleScroll);
    }, [navLinks]);

    // Lock body scroll when mobile menu is open
    useEffect(() => {
        if (mobileMenuOpen) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = '';
        }
        return () => { document.body.style.overflow = ''; };
    }, [mobileMenuOpen]);

    return (
        <div
            ref={containerRef}
            id="home"
            className={cn(
                'relative flex h-screen w-full flex-col items-center justify-between overflow-hidden bg-background p-8 font-sans md:p-12',
                className
            )}
        >
            {/* Particles background */}
            <Particles count={60} />

            {/* Spotlight effect */}
            <Spotlight size={600} className="z-[1]" />

            {/* Header */}
            <header className="z-30 flex w-full max-w-7xl items-center justify-between">
                <LogoWithName />
                <div className="hidden items-center space-x-8 md:flex">
                    {navLinks.map((link) => (
                        <NavLink
                            key={link.label}
                            href={link.href}
                            isActive={activeSection === link.href.replace('#', '')}
                        >
                            {link.label}
                        </NavLink>
                    ))}
                </div>
                <div className="flex items-center gap-4">
                    {themeToggle}
                    {/* Hamburger — mobile only */}
                    <motion.button
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ duration: 0.5 }}
                        onClick={() => setMobileMenuOpen(prev => !prev)}
                        className="relative z-[200] flex h-8 w-8 flex-col items-center justify-center gap-1.5 md:hidden"
                        aria-label={mobileMenuOpen ? 'Close menu' : 'Open menu'}
                    >
                        <motion.span
                            animate={mobileMenuOpen
                                ? { rotate: 45, y: 6, width: '24px' }
                                : { rotate: 0, y: 0, width: '24px' }
                            }
                            transition={{ duration: 0.3, ease: [0.22, 1, 0.36, 1] }}
                            className="block h-0.5 bg-foreground origin-center"
                            style={{ width: 24 }}
                        />
                        <motion.span
                            animate={mobileMenuOpen
                                ? { opacity: 0, scaleX: 0 }
                                : { opacity: 1, scaleX: 1 }
                            }
                            transition={{ duration: 0.2 }}
                            className="block h-0.5 bg-foreground"
                            style={{ width: 24 }}
                        />
                        <motion.span
                            animate={mobileMenuOpen
                                ? { rotate: -45, y: -6, width: '24px' }
                                : { rotate: 0, y: 0, width: '20px' }
                            }
                            transition={{ duration: 0.3, ease: [0.22, 1, 0.36, 1] }}
                            className="block h-0.5 bg-foreground origin-center"
                            style={{ width: 20 }}
                        />
                    </motion.button>
                </div>
            </header>

            {/* ── Mobile Menu Overlay ── */}
            <motion.div
                initial={false}
                animate={mobileMenuOpen ? { x: '0%' } : { x: '100%' }}
                transition={{ duration: 0.45, ease: [0.22, 1, 0.36, 1] }}
                className="fixed inset-0 z-[150] flex flex-col bg-background md:hidden"
            >
                {/* Backdrop blur tint */}
                <motion.div
                    animate={{ opacity: mobileMenuOpen ? 1 : 0 }}
                    transition={{ duration: 0.3 }}
                    className="absolute inset-0 bg-background/95 backdrop-blur-xl"
