                {/* Content */}
                <div className="relative z-10 flex flex-col h-full px-8 pt-28 pb-12">
                    {/* Nav links — staggered */}
                    <nav className="flex flex-col gap-2 flex-1">
                        {navLinks.map((link, i) => (
                            <motion.a
                                key={link.label}
                                href={link.href}
                                onClick={() => setMobileMenuOpen(false)}
                                initial={{ opacity: 0, x: 40 }}
                                animate={mobileMenuOpen
                                    ? { opacity: 1, x: 0, transition: { delay: 0.1 + i * 0.07, duration: 0.4, ease: [0.22, 1, 0.36, 1] } }
                                    : { opacity: 0, x: 40 }
                                }
                                className={cn(
                                    'group flex items-center justify-between border-b border-foreground/10 py-5 text-4xl font-semibold tracking-tight transition-colors',
                                    activeSection === link.href.replace('#', '')
                                        ? 'text-foreground'
                                        : 'text-foreground/40 hover:text-foreground'
                                )}
                            >
                                <span>{link.label}</span>
                                <motion.span
                                    className="text-xl opacity-0 group-hover:opacity-100 transition-opacity"
                                    initial={{ x: -8 }}
                                    whileHover={{ x: 0 }}
                                >
                                    →
