                    {/* Typewriter role */}
                    <div className="mt-3 text-xs font-mono text-foreground/40 tracking-widest">
                        <Typewriter
                            words={['Python Full Stack Intern', 'Problem Solver', 'Backend Explorer', 'Open Source Enthusiast']}
